mapper.delete:
delete from user_info_edu where edu_idx in (select edu_idx from user_info_edu where edu_idx not in
#{eduList.edu_idx}
and user_idx=#{user_idx})

delete from user_info_qualifi where qualifi_idx in (select qualifi_idx from user_info_qualifi where qualifi_idx not in
#{qualifiList.qualifi_idx}
and user_idx=#{user_idx})

mapper.update:
	update user_info_qualifi
	set 
		qualifi_name = #{qualifi_name}
		,qualifi_getdate = #{qualifi_getdate}
	where qualifi_idx = #{qualifi_idx}

update user_info_edu
set
	edu_school = #{edu_school}
	,edu_status = #{edu_status}
	,edu_year = #{edu_year}
	,edu_month = #{edu_month}
where edu_idx = #{edu_idx}

update user_info
set 
	user_name=#{user_name}
	,user_sex=#{user_sex}
	,user_comp=#{user_comp}
	,user_comp_enter=#{user_comp_enter}
	,user_dept=#{user_dept}
	,user_spot=#{user_spot}
	,user_army=#{user_army}
	,user_marriage=#{user_marriage}
	,user_army_start=#{user_army_start}
	,user_army_end=#{user_army_end}
	,user_army_part=#{user_army_part}
	,user_telephone=#{user_telephone}
	,user_mobile=#{user_mobile}
	,user_email=#{user_email}
	,user_address=#{user_address}
	,user_social_number=#{user_social_number}
	,user_zipcode=#{user_zipcode}
where user_idx=#{user_idx}

mapper.insert:
insert into user_info
	(user_idx
	,user_register
	,user_name
	,user_sex
	,user_comp
	,user_comp_enter
	,user_dept
	,user_spot
	,user_army
	,user_marriage
	,user_army_start
	,user_army_end
	,user_army_part
	,user_telephone
	,user_mobile
	,user_email
	,user_address
	,user_social_number
	,user_zipcode)
values 
	((select nvl(max(user_idx)+1,1) from user_info)
	,SYSDATE
	,#{user_name}
	,#{user_sex}
	,#{user_comp}
	,#{user_comp_enter}
	,#{user_dept}
	,#{user_spot}
	,#{user_army}
	,#{user_marriage}
	,#{user_army_start}
	,#{user_army_end}
	,#{user_army_part}
	,#{user_telephone}
	,#{user_mobile}
	,#{user_email}
	,#{user_address}
	,#{user_social_number}
	,#{user_zipcode})
